class Solution:
    def getHint(self, secret: str, guess: str) -> str:
        bulls, cows = 0, 0
        arr = [0] * 10
        for i in range(len(secret)):
            if secret[i] == guess[i]:
                bulls += 1
            else:
                # 0guess
                if arr[int(secret[i])] < 0:
                    cows += 1
                arr[int(secret[i])] += 1
                # 0secret
                if arr[int(guess[i])] > 0:
                    cows += 1
                arr[int(guess[i])] -= 1
        return f'{bulls}A{cows}B'
