public class Solution {
    public string GetHint(string secret, string guess) {
        int bulls = 0;
        int cows = 0;
        int[] arr = new int[10];
        for (int i = 0; i < secret.Length; i++){
            if (secret[i] == guess[i]) ++bulls;
            else{
                // 0guess
                if (arr[secret[i]-'0']++ < 0) cows++;
                // 0secret
                if (arr[guess[i]-'0']-- > 0) cows++;
            }
        }
        return $"{bulls}A{cows}B";
    }
}
